		public static void HideElement(UIElement obj)
		{
			obj.IsEnabled = false;
			obj.Visibility = Visibility.Hidden;
		}

		public static void UnHideElement(UIElement obj)
		{
			obj.IsEnabled = true;
			obj.Visibility = Visibility.Visible;
		}

		/// <summary>
		/// Создание <see cref="RadioButton"/> из <see cref="List{T}"/> для <see cref="StackPanel"/> - метод расширения
		/// </summary>
		/// <param name="sp">Родительский <see cref="StackPanel"/></param>
		/// <param name="topics"><see cref="List{T}"/> содержащий <see cref="TopicDM"/> для генерации <see cref="RadioButton"/></param>
		public static void AppendRadioButton(this StackPanel sp, List<TopicDM> topics)
		{
			foreach (TopicDM topic in topics)
			{
				RadioButton rb = new RadioButton()
				{
					Content = topic.content,
					Name = topic.title,
					Margin = new Thickness(5),
					GroupName = "Topics",
					VerticalContentAlignment = VerticalAlignment.Center
				};
				rb.Checked += (s, e) =>
				{
					ExaminationSwitcher.SelectedTopic = rb.Name;
				};
				sp.Children.Add(rb);
			}
		}

		/// <summary>
		/// Создание <see cref="RadioButton"/> из <see cref="List{T}"/> для <see cref="StackPanel"/> - метод расширения
		/// </summary>
		/// <param name="sp">Родительский <see cref="StackPanel"/></param>
		/// <param name="topics"><see cref="List{T}"/> содержащий <see cref="string"/> для генерации <see cref="RadioButton"/></param>
		public static void AppendRadioButton(this StackPanel sp, List<string> options)
		{
			foreach (string option in options)
			{
				RadioButton rb = new RadioButton()
				{
					Content = option,
					Margin = new Thickness(5),
					GroupName = "Options",
					FontSize = 20,
					VerticalContentAlignment = VerticalAlignment.Center
				};
				rb.Checked += (s, e) =>
				{
					Exam.Pages.Start.selectedOption = rb.Content.ToString();
				};
				sp.Children.Add(rb);
			}
		}

		/// <summary>
		/// Создание <see cref="RadioButton"/> из <see cref="List{T}"/> для <see cref="StackPanel"/> отображающее правильный и текущий ответ- метод расширения
		/// </summary>
		/// <param name="sp">Родительский <see cref="StackPanel"/></param>
		/// <param name="topics"><see cref="List{T}"/> содержащий <see cref="string"/> для генерации <see cref="RadioButton"/></param>
		public static void AppendRadioButton(this StackPanel sp, List<string> options, int[] opt)
		{
			foreach (string option in options)
			{
				SolidColorBrush rbColor = new SolidColorBrush(Colors.White);
				if (options.IndexOf(option) == opt[0])
				{
					rbColor = new SolidColorBrush(Colors.Green);
				}

				if (opt[0] != opt[1] && options.IndexOf(option) == opt[1])
				{
					rbColor = new SolidColorBrush(Colors.Red);
				}
					RadioButton rb = new RadioButton()
				{
					Content = option,
					Margin = new Thickness(5),
					GroupName = "Options",
					FontSize = 20,
					Background = rbColor,
					VerticalContentAlignment = VerticalAlignment.Center
				};
				rb.Checked += (s, e) =>
				{
					Exam.Pages.Start.selectedOption = rb.Content.ToString();
				};
				sp.Children.Add(rb);
			}
		}

